public class Commands {

    public static Message getContent(String command) {
        MessageImpl msg = new MessageImpl();
        String hexString;
        switch (command) {
            case "register":
                hexString = "7E0100002C0646205194820CE2000000000000000000484C363631000000000000000000000000000000363647303034340036364730303434D17E";
                break;
            case "authentication":

                hexString = auth();
                hexString = "7E01020004064620519482192B34323531107E";
                //hexString = "7E0102002006462051948216104E6A51324D6A41314D546B304F44493D2E6C465330616F5167386E6645507771537E";
                //           7E010200200646205194821BF04E6A51324D6A41314D546B304F44493D2E6C465330616F5167386E6645507771BE7E
                break;
            case "heartbeat":
                hexString = "7E000200000134123456782C8F9C7E";
                break;
            case "logoff":
                hexString = "7E00030000013412345678000A347E";
                break;
            case "location":
                hexString="7E0200003C0646205194821A0900000000000400030161DCD206B9D0FC001A0000005C161103140947010400000000030200002504000000002A0204002B040000000030011631010BD27E";
                break;
            case "can1":
                hexString="7E0C20003301592417229500601C000000000004000301CEC1C10727E551002C000000B31610142146301610142146300000000198FF50E50210010000000000BC7E";
                break;
            case "can2":
                hexString="" ;
                break;
            case "passthrough":
                hexString="";
                break;
            default:
                hexString = null;
        }
        if (hexString == null) {
            return null;
        }
        try {
            msg.fromHexString(hexString, true);
        } catch (ProtoException e) {
            e.printStackTrace();
        }
        return msg;
    }

    private static String auth() {
        String code = HexConverter.bytesToHexString("3299".getBytes());
        Body auth = new TerminalAuthentication(code);
        MessageImpl msg = (MessageImpl) MessageBuilder.create("64620519468", 5, auth);
        String s = msg.toHexString(true);
        System.out.println(s);
        return s;
    }


}
